import * as echarts from "echarts";
export default {
  name: 'Echarts',
  mounted: function mounted() {
    this.Echarts();
  },
  data: function data() {
    return {
      dateX: [],
      valueY: [],
      chart: ''
    };
  },
  methods: {
    Echarts: function Echarts() {
      var _this = this;

      this.request.get("/data/find").then(function (res) {
        for (var i = 0; i < res.length; i++) {
          _this.dateX.push(res[i].createDate);

          _this.valueY.push(res[i].senValue);
        }

        console.log(_this.dateX);
        console.log(_this.valueY);
        _this.chart = echarts.init(document.getElementById('main'));
        var option = {
          title: {
            text: '温度采集'
          },
          tooltip: {},
          legend: {
            data: ['温度曲线']
          },
          dataZoom: [{
            show: true,
            realtime: true,
            start: 65,
            end: 85
          }, {
            type: 'inside',
            realtime: true,
            start: 65,
            end: 85
          }],
          xAxis: {
            type: 'category',
            data: _this.dateX.map(function (str) {
              return str.replace(' ', '\n');
            })
          },
          yAxis: {},
          series: [{
            name: '温度曲线',
            type: 'line',
            data: _this.valueY
          }]
        };

        _this.chart.setOption(option);
      });
    }
  }
};
