import tempTable from "@/components/tempTable";
export default {
  name: "tempChart",
  components: {
    tempTable: tempTable
  },
  prop: ['senValue', 'createDate'],
  mounted: function mounted() {
    this.initChart();
    this.updateChart();
  },
  data: function data() {
    return {
      senValue: this.senValue,
      createDate: this.createDate
    };
  },
  methods: {
    initChart: function initChart() {
      this.chartInstance = this.$echarts.init(this.$refs.ref);
    },
    updateChart: function updateChart() {
      var option = {
        xAxis: {
          type: 'time',
          data: this.createDate
        },
        yAxis: {
          type: 'value'
        },
        series: [{
          data: this.senValue,
          type: 'line'
        }]
      };
      this.chartInstance.setOption(option);
    }
  }
};
