export default {
  name: 'Echarts',
  dateX: [],
  valueY: [],
  data: function data() {
    return {
      dateX: [],
      valueY: []
    };
  },
  created: function created() {
    this.Echarts();
  },
  methods: {
    Echarts: function Echarts() {
      var _this = this;

      var myChart = this.$echarts.init(this.$refs.a);
      this.request.get("/data/find").then(function (res) {
        for (var i = 0; i < res.length; i++) {
          _this.dateX.push(res[i].createDate);

          _this.valueY.push(res[i].valueY);
        }
      });
      var option = {
        title: {
          text: '温度采集'
        },
        tooltip: {},
        legend: {
          data: ['温度曲线']
        },
        xAxis: {
          type: 'time',
          data: this.dateX
        },
        yAxis: {},
        series: [{
          name: '温度曲线',
          type: 'line',
          data: this.valueY
        }]
      };
      myChart.setOption(option);
    }
  },
  mounted: function mounted() {
    this.Echarts();
  }
};
