import * as echarts from "echarts";
export default {
  name: 'Echarts',
  mounted: function mounted() {
    this.Echarts();
  },
  data: function data() {
    return {
      dateX: [],
      valueY: [],
      chart: ''
    };
  },
  methods: {
    Echarts: function Echarts() {
      var _this = this;

      this.request.get("/data/find").then(function (res) {
        for (var i = 0; i < res.length; i++) {
          _this.dateX.push(res[i].createDate);

          _this.valueY.push(res[i].valueY);
        }

        _this.chart = echarts.init(document.getElementById('main'));
        var option = {
          title: {
            text: '温度采集'
          },
          tooltip: {},
          legend: {
            data: ['温度曲线']
          },
          xAxis: {
            type: 'time',
            data: _this.dateX
          },
          yAxis: {},
          series: [{
            name: '温度曲线',
            type: 'line',
            data: _this.valueY
          }]
        };

        _this.chart.setOption(option);
      });
    }
  }
};
