export default {
  name: 'Echarts',
  created: function created() {
    this.Echarts();
  },
  data: function data() {
    return {
      dateX: [],
      valueY: []
    };
  },
  methods: {
    Echarts: function Echarts() {
      var _this = this;

      this.request.get("/data/find").then(function (res) {
        _this.dateX = res.createDate;
        _this.valueY = res.sen;
      }, this.setChart());
      setChart();
      {
        var myChart = this.$echarts.init(document.getElementById('main'));
        var option = {
          title: {
            text: '温度采集'
          },
          tooltip: {},
          legend: {
            data: ['温度曲线']
          },
          xAxis: {
            data: this.dateX
          },
          yAxis: {},
          series: [{
            name: '温度曲线',
            type: 'line',
            data: this.valueY
          }]
        }; // 使用刚指定的配置项和数据显示图表。

        myChart.setOption(option);
      }
    }
  },
  mounted: function mounted() {}
};
