import tempTable from "@/components/tempTable";
import * as echarts from "echarts";
export default {
  name: "tempChart",
  mCharts: echarts.init(document.querySelector('div')),
  components: {
    tempTable: tempTable
  },
  props: ['senValue', 'createDate'],
  mounted: function mounted() {
    this.initChart();
    this.updateChart();
  },
  data: function data() {
    return {
      senValue: this.senValue,
      createDate: this.createDate
    };
  },
  methods: {
    initChart: function initChart() {},
    updateChart: function updateChart() {
      // const X =this.allData.map((item)=> {return item.createDate})
      // const Y =this.allData.map((item)=> {return item.senValue})
      var option = {
        xAxis: {
          type: 'category',
          data: this.createDate
        },
        yAxis: {
          type: 'value'
        },
        series: [{
          name: '温度采集曲线',
          data: this.senValue,
          type: 'line'
        }]
      };
      this.mCharts.setOption(option);
    }
  }
};
