  mounted: function mounted() {
    this.Echarts();
    this.getDate();
  },
  methods: {
    //res本身就是个对象数组
    getDate: function getDate() {
      var _this = this;

      this.request.get("/data/find").then(function (res) {
        console.log(res);
        _this.list = res.data. //这后面加那个对象或数组
        数据差一个名称; //

        for (i = 0; i < res.length; i++) {
          _this.dateX.push(res[i].createDate);

          _this.valueY.push(res[i].valueY);
        }
      });
    },
    Echarts: function Echarts() {
      var myChart = this.$echarts.init(document.getElementById('main'));
      var option = {
        //myChart,option,你只是定义了，没有用到 这两颜色都是灰的
        // let 定义变量，你直接定义了一堆对象
