        title: {
          text: '温度采集'
        },
        tooltip: {},
        legend: {
          data: ['温度曲线']
        },
        xAxis: {
          type: 'time',
          data: this.dateX
        },
        yAxis: {},
        series: [{
          name: '温度曲线',
          type: 'line',
          data: this.valueY
        }]
      };
      myChart.setOption(option);
    }
  }
